import React from 'react';
import './ConferenceCreator.css';
import axios from "axios";

export class ConferenceCreator extends React.Component{

    constructor(props) {
        super(props);

        this.url = 'http://localhost:8762';

        this.state = {

        }
    }

    componentDidMount() {
    }

    onCreate = (event) => {
        let createUrl = `${this.url}/plot-accounter-plot/plot/`;
        let creationForm = event.target.parentElement;
        let createData = {
            name: creationForm.name.value,
            participants: [],
            conferenceRoom: {
                id: creationForm.conferenceRoom.value,
            },
            conferenceDate: creationForm.date.value,
        };
        axios.post(createUrl, createData, {withCredentials: true})
            .then(() => this.props.history.push("/"));
    };

    render() {
        return(
            <div>
                <h2 className="conference-list-header">New Plot</h2>
                <div className="conference-creation-form-container">
                    <form>
                        <div className="form-group">
                            <label htmlFor="newPlotAdr">Address:</label>
                            <input name="name" type="text" className="form-control" id="newPlotAdr"
                                   placeholder=""/>
                        </div>
                        <div className="form-group">
                            <label htmlFor="newPlotPrice">Price:</label>
                            <input name="name" type="text" className="form-control" id="newPlotPrice"
                                   placeholder=""/>
                        </div>
                        <div className="form-group">
                            <label htmlFor="newPlotHectares">Hectares:</label>
                            <input name="name" type="text" className="form-control" id="newPlotHectares"
                                   placeholder=""/>
                        </div>
                        <div className="custom-control custom-switch">
                            <input type="checkbox" className="custom-control-input" id="newPlotSold"/>
                            <label className="custom-control-label" htmlFor="newPlotSold">Is plot already sold?</label>
                        </div>
                        <br/>
                        <button onClick={this.onCreate} type="button" className="btn btn-primary">Create</button>
                    </form>
                </div>
            </div>
        );
    }

}
