import React from 'react';
import './ConferenceDeatils.css';
import axios from "axios";

export class ConferenceDetails extends React.Component{

    constructor(props) {
        super(props);

        this.state = {
            conferenceId: props.match.params.conferenceId,
            conference: {},
        };
    }

    componentDidMount() {
        this.tempLogin().catch(() => this.initParams());
    }

    tempLogin = () => {
        let loginUrl = `${this.urlBase}:${this.port}/login`;
        let loginData = {
            username: 'user1@gmail.com',
            password: 'user1',
        };
        return axios.post(loginUrl, loginData, {withCredentials: true, auth: loginData})
            .then((response) => console.log(response));
    };

    initParams = () => {
        this.loadConference()
            .then((response) => this.setState({conference: response.data}));
    };

    loadConference = () => {
        let loadUrl = `${this.urlBase}:${this.port}/api/v1/conference/${this.state.id}`;
        return axios.get(loadUrl, {withCredentials: true});
    };

    render() {
        return(
            <div>
                <h2 className="conference-details-header">Conference '{this.state.conference.name}' Details</h2>
                <h3 className="conference-details-room-header">Conference Room</h3>
                <ul>
                    <li>Room name: {this.state.conference.conferenceRoom.roomName}</li>
                    <li>Location: {this.state.conference.conferenceRoom.location}</li>
                </ul>
                <h3 className="conference-details-participants-header">Participants</h3>
            </div>
        );
    }

}
